import React from 'react';
import { Col, Row, Image, Typography } from 'antd';
import { useDarkMode } from '../../../../Hooks/useDarkMode';
import { useTranslation } from 'react-i18next';
import { AntdTag } from '../../../../components/antd';
import { DefaultLogo } from '../../../../components';
import { EditOutlined } from '@ant-design/icons';
import { Colors } from '../../../colors';

const { Text } = Typography;

export default function CompanyInfo(props) {
  const [mode] = useDarkMode();
  const { t } = useTranslation();
  return (
    <Row
      className={
        mode === 'dark'
          ? 'account_setting_drawer_hover_dark account_setting_drawer_name'
          : 'account_setting_drawer_hover account_setting_drawer_name'
      }
      onDoubleClick={props.onClickName}
    >
      <Col span={24}>
        <Row>
          <Col lg={5} sm={14} xs={24}>
            <Text strong={true}> {t('Company.Company_setting_title')}</Text>
          </Col>
          <Col sm={7} xs={10}>
            {t('Company.Company_logo')}
          </Col>
          <Col lg={12} sm={10} xs={14}>
            <Row justify='space-between'>
              <Col span={8}>
                {props?.data?.logo ? (
                  <Image
                    width={90}
                    src={props?.data?.logo}
                    fallback='data:image/png;base64,'
                  >
                    {t('Company.Logo')}
                  </Image>
                ) : (
                  <DefaultLogo size={75} style={{ backgroundColor: '' }} />
                )}
              </Col>
              <Col>
                <EditOutlined className='icon' onClick={props.onClickName} />
              </Col>
            </Row>
          </Col>
        </Row>
      </Col>
      <Col span={24}>
        <Row className='line_height'>
          <Col lg={5} sm={6} xs={0}></Col>
          <Col lg={7} sm={8} xs={10}>
            <Text>{t('Company.Company_name')}</Text>
            <br />

            <Text> {t('Expenses.Suppliers.Business_id_no')}</Text>
            <br />
            <Text> {t('Company.Company_type')}</Text>
            <br />
            <Text> {t('Company.Company_email')}</Text>
            <br />

            <Text> {t('Company.Company_mobile')}</Text>
            <br />
            <Text> {t('Form.Website')}</Text>
            <br />
            <Text> {t('Company.Slogan')}</Text>
            <br />
            <Text> {t('Company.Social_media')}</Text>
            <br />
            <Text> {t('Company.Company_address')}</Text>
          </Col>
          <Col lg={12} sm={10} xs={14}>
            {' '}
            <Text>{props.data?.fa_name} </Text>
            <br />
            <Text>{props.data?.business_number}</Text>
            <br />
            <Text>
              {props.data?.type === 'supermarket'
                ? t('Company.Form.Supermarket')
                : props.data?.type === 'commercial'
                  ? t('Company.Form.Commercial')
                  : ''}
            </Text>
            <br />
            <Text> {props.data?.email}</Text>
            <br />
            <Text>
              {props.data?.mobile?.mobile_list?.map((item) => (
                <AntdTag color={Colors.primaryColor} key={item}>
                  {item}
                </AntdTag>
              ))}
            </Text>
            <br />
            <Text> {props.data?.website}</Text>
            <br />
            <Text> {props.data?.slogan}</Text>
            <br />
            {props.data?.social_media?.social_media_list?.map((item, index) => (
              <Text color='blue' key={index}>
                {item?.social_name} : {item?.social_address}&nbsp;&nbsp;
              </Text>
            ))}
            <br />
            {props.data?.address?.address_list?.map((item, index) => (
              <Text key={index}>
                {item?.fa_name} : {item?.address_place}
                <br />
              </Text>
            ))}
          </Col>
        </Row>
      </Col>
    </Row>
  );
}
