import React from 'react';
import { Row, Col, Table, Image, Typography, Descriptions } from 'antd';
import PrintComponent from './Print';
import moment from 'moment';
import { useTranslation } from 'react-i18next';
import { useGetCompanyInfo, useGetUserInfo } from '../../Hooks';
import { DefaultLogo } from '../../components';

const { Title } = Typography;
const PrintTable = (props) => {
  const { t } = useTranslation();

  //get company information
  const { data } = useGetCompanyInfo();

  //get user information
  const useInfo = useGetUserInfo();

  const BodyTable = (props) => {
    return <thead {...props} style={{ fontSize: '11px' }} />;
  };

  const components = {
    header: {
      wrapper: BodyTable,
    },
  };

  const getPageMargins = () => {
    return `@page { margin: 4mm 7mm !important; }`;
  };

  return (
    <PrintComponent ref={props.printRef}>
      <div>
        <div className='page-footer' style={{ pageBreakAfter: 'always' }}>
          <Row
            justify='center'
            align='middle'
            style={{ width: '100%', height: '100%' }}
          >
            <Col>
              {t(
                'Sales.All_sales.Invoice.Chanar_accounting_product_of_microcis',
              )}
            </Col>
            {/* <Col span={8} style={{ textAlign: "center" }}>
              <span className="page-number">
                <span className="pageCounter"></span>
              </span>
            </Col>
            <Col span={8}></Col> */}
          </Row>
        </div>
        <table style={{ width: '100%' }}>
          <thead>
            <tr>
              <td>
                <div className='page-header-space'></div>
                <style>{getPageMargins()}</style>
              </td>
            </tr>
          </thead>

          <tbody>
            <tr>
              <td>
                <div>
                  {!props.filters ? (
                    <Row style={{ paddingBottom: '12px' }}>
                      <Col span={9}>
                        <Row>
                          <Col>
                            <Descriptions
                              layout='horizontal'
                              style={
                                t('Dir') === 'ltr'
                                  ? { width: '260px', paddingTop: '40px' }
                                  : { width: '240px', paddingTop: '40px' }
                              }
                              column={{
                                xxl: 1,
                                xl: 1,
                                lg: 1,
                                md: 1,
                                sm: 2,
                                xs: 1,
                              }}
                              size='small'
                            >
                              <Descriptions.Item label={t('Form.Printed_by')}>
                                {useInfo?.data?.username}
                              </Descriptions.Item>
                            </Descriptions>
                          </Col>
                        </Row>
                      </Col>

                      <Col span={6}>
                        <Row justify='center'>
                          <Col>
                            {data?.logo ? (
                              <Image
                                width={90}
                                src={data?.logo}
                                style={{
                                  maxHeight: '90px',
                                  marginBottom: '5px',
                                }}
                                fallback='data:image/png;base64,'
                              />
                            ) : (
                              <DefaultLogo size={65} />
                            )}
                          </Col>
                        </Row>
                        <Row justify='center'>
                          <Col style={{ textAlign: 'center' }}>
                            <Title level={4}>{props.title}</Title>
                          </Col>
                        </Row>
                      </Col>
                      <Col span={9}>
                        <Row justify='end'>
                          <Col>
                            <Descriptions
                              layout='horizontal'
                              style={
                                t('Dir') === 'ltr'
                                  ? { width: '215px', paddingTop: '40px' }
                                  : { width: '200px', paddingTop: '40px' }
                              }
                              column={{
                                xxl: 1,
                                xl: 1,
                                lg: 1,
                                md: 1,
                                sm: 2,
                                xs: 1,
                              }}
                              size='small'
                            >
                              <Descriptions.Item
                                label={t(
                                  'Sales.All_sales.Invoice.Date_and_time',
                                )}
                              >
                                {moment().format('YYYY-MM-DD HH:mm')}
                              </Descriptions.Item>
                            </Descriptions>
                          </Col>
                        </Row>
                      </Col>
                    </Row>
                  ) : (
                    <Row style={{ paddingBottom: '12px' }}>
                      <Col span={9}>{props.filters}</Col>

                      <Col span={6}>
                        <Row justify='center'>
                          <Col>
                            {data?.logo ? (
                              <Image
                                width={90}
                                src={data?.logo}
                                style={{
                                  maxHeight: '90px',
                                  marginBottom: '5px',
                                }}
                                fallback='data:image/png;base64,'
                              />
                            ) : (
                              <DefaultLogo size={65} />
                            )}
                          </Col>
                        </Row>
                        <Row justify='center'>
                          <Col style={{ textAlign: 'center' }}>
                            <Title level={4}>{props.title}</Title>
                          </Col>
                        </Row>
                      </Col>
                      <Col span={9}>
                        <Row justify='end'>
                          <Col>
                            <Descriptions
                              layout='horizontal'
                              style={
                                t('Dir') === 'ltr'
                                  ? { width: '215px', paddingTop: '40px' }
                                  : { width: '200px', paddingTop: '40px' }
                              }
                              column={{
                                xxl: 1,
                                xl: 1,
                                lg: 1,
                                md: 1,
                                sm: 2,
                                xs: 1,
                              }}
                              size='small'
                            >
                              <Descriptions.Item
                                label={t(
                                  'Sales.All_sales.Invoice.Date_and_time',
                                )}
                              >
                                {moment().format('YYYY-MM-DD HH:mm')}
                              </Descriptions.Item>
                              <Descriptions.Item label={t('Form.Printed_by')}>
                                {useInfo?.data?.username}
                              </Descriptions.Item>
                            </Descriptions>
                          </Col>
                        </Row>
                      </Col>
                    </Row>
                  )}
                  <Row style={{ width: '100%' }}>
                    <Col span={24}>
                      <Table
                        dataSource={props?.dataSource}
                        pagination={false}
                        columns={!props?.columns ? false : props?.columns}
                        rowKey={(record) => record.id || record?.serial}
                        // tableLayout="auto"
                        rowClassName={() => 'print-table-column'}
                        bordered
                        size='small'
                        components={components}
                      >
                        {props?.domColumns}
                      </Table>
                    </Col>
                  </Row>
                  {props.selectResult && (
                    <Table
                      dataSource={props?.resultDataSource}
                      pagination={false}
                      columns={!props?.columns ? false : props?.resultColumns}
                      style={{ fontSize: '11px' }}
                      bordered
                      size='small'
                      components={components}
                      rowClassName={() => 'print-table-column'}
                    >
                      {props.resultDomColumns}
                    </Table>
                  )}
                </div>
              </td>
            </tr>
          </tbody>

          <tfoot>
            <tr>
              <td>
                <div className='page-footer-space'></div>
              </td>
            </tr>
          </tfoot>
        </table>
      </div>
    </PrintComponent>
  );
};
export default PrintTable;
