import React from 'react';
import { Card, Image, Tooltip, Typography } from 'antd';
import Icon from '@ant-design/icons';
import { HeartTwoTone } from '@ant-design/icons';
import { useDarkMode } from '../../../../Hooks/useDarkMode';

const HeartSvg = () => (
  <svg width='1em' height='1em' fill='currentColor' viewBox='0 0 1024 1024'>
    <path d='M923 283.6c-13.4-31.1-32.6-58.9-56.9-82.8-24.3-23.8-52.5-42.4-84-55.5-32.5-13.5-66.9-20.3-102.4-20.3-49.3 0-97.4 13.5-139.2 39-10 6.1-19.5 12.8-28.5 20.1-9-7.3-18.5-14-28.5-20.1-41.8-25.5-89.9-39-139.2-39-35.5 0-69.9 6.8-102.4 20.3-31.4 13-59.7 31.7-84 55.5-24.4 23.9-43.5 51.7-56.9 82.8-13.9 32.3-21 66.6-21 101.9 0 33.3 6.8 68 20.3 103.3 11.3 29.5 27.5 60.1 48.2 91 32.8 48.9 77.9 99.9 133.9 151.6 92.8 85.7 184.7 144.9 188.6 147.3l23.7 15.2c10.5 6.7 24 6.7 34.5 0l23.7-15.2c3.9-2.5 95.7-61.6 188.6-147.3 56-51.7 101.1-102.7 133.9-151.6 20.7-30.9 37-61.5 48.2-91 13.5-35.3 20.3-70 20.3-103.3 0.1-35.3-7-69.6-20.9-101.9z' />
  </svg>
);
const HeartIcon = (props: any) => <Icon component={HeartSvg} {...props} />;
interface IProps {
  item: any;
  onChangePin: (id: number, is_pine: boolean, type: string) => void;
  type: string;
  name: string;
  photo: string;
  id: number;
  isPine: boolean;
}
function ProductItem(props: IProps) {
  const imgEl = React.useRef<HTMLImageElement>(null);
  const [loaded, setLoaded] = React.useState(false);
  const [mode] = useDarkMode();

  const onImageLoaded = () => setLoaded(true);

  React.useEffect(() => {
    const imgElCurrent = imgEl.current;

    if (imgElCurrent) {
      imgElCurrent.addEventListener('load', onImageLoaded);
      return () => imgElCurrent.removeEventListener('load', onImageLoaded);
    }
  }, [imgEl]);

  const onClickPin = (e: any) => {
    e.stopPropagation();
  };
  return (
    <>
      <Card
        hoverable
        style={{ borderRadius: '8px', backgroundColor: '#f5f5f5' }}
        // loading={props.isLoading}
        bodyStyle={mode === 'dark' ? styles.cardBody1 : styles.cardBody}
        size='small'
        bordered={false}
        cover={
          <span style={styles.cardCover}>
            <Image
              //@ts-ignore
              ref={imgEl}
              style={loaded ? styles.cardImage2 : styles.cardImage}
              src={`${props?.photo}`}
              alt='product'
              onLoad={onImageLoaded}
              preview={false}
              width={'100%'}
              fallback='data:image/png;base64,'
            />

            <Image
              width={'100%'}
              height={!loaded ? 85 : 30}
              style={styles.cardImage}
              preview={false}
              src='error'
              fallback='data:image/png;base64,'
            />
          </span>
        }
      >
        <Card.Meta
          description={
            <Tooltip title={props.item?.name}>
              <Typography.Paragraph
                //@ts-ignore
                ellipsis={{ rows: 2, tooltip: props?.name }}
                //@ts-ignore
                style={styles.cardTitle}
              >
                {props?.name}
              </Typography.Paragraph>
            </Tooltip>
          }
        />
      </Card>
      <span className='love' onClick={onClickPin}>
        {props?.isPine ? (
          <HeartIcon
            onClick={() =>
              props?.onChangePin(props?.id, props?.isPine, props.type)
            }
            style={{
              color: 'hotpink',
              fontSize: '20px',
            }}
          />
        ) : (
          <HeartTwoTone
            twoToneColor='#eb2f96'
            onClick={() =>
              props?.onChangePin(props?.id, props?.isPine, props.type)
            }
          />
        )}
      </span>
    </>
  );
}
//@ts-ignore
// eslint-disable-next-line no-func-assign
ProductItem = React.memo(ProductItem);

export default ProductItem;

const styles = {
  margin: { marginBottom: '4px' },
  cardBody: {
    padding: '10px 5px',
    height: '50px',
    backgroundColor: '#fff',
    lineHeight: '20px',
    borderRadius: '6px',
  },
  cardBody1: {
    padding: '10px 5px',
    height: '50px',
    lineHeight: '20px',
    backgroundColor: '#282e33',
  },
  cardTitle: {
    margin: '0px',
    fontSize: '12px',
    textAlign: 'start',
    padding: '0px 4px',
    lineHeight: '18px',
  },
  cardImage: {
    height: '84px',
    width: '100%',
    borderRadius: '6px 6px 0px 0px',
    display: 'none',
  },
  cardImage1: {
    height: '84px',
    width: '100%',
    borderRadius: '6px 6px 0px 0px',
    display: 'block',
  },
  cardImage2: {
    height: '84px',
    width: '100%',
    borderRadius: '6px 6px 0px 0px',
    display: 'inline-block',
  },
  productItem: { paddingInlineEnd: '10px' },
  cardCover: {
    height: '84px',
  },
};
